val sym2InitPrice = data.groupBy(_.sym).mapValues{ data => data.sortBy(_.Date.getTime).head.Close}

val investedAmountPerSymbol = sym2Shares.map { case (symbol, shares) =>  (symbol, shares.toInt * sym2InitPrice.getOrElse(symbol, 0.0)) }.toMap
val totalinvestedAmt = investedAmountPerSymbol.map { case (symbol,investedAmount) => investedAmount }.sum

val portfolioShareQty = sym2Shares.map(x=> x._2.toDouble).reduce(_+_)
val totalAmountInvested = sym2Shares.map{case (symbol, shares) =>  shares.toInt * sym2InitPrice.getOrElse(symbol, 0.0) }.sum

// stock Ratio in Portfolio
val sym2PortfolioRatio = sym2Shares.map{ case (symbol, shares) => (symbol, ((shares.toInt * sym2InitPrice.getOrElse(symbol, 0.0)).toDouble / totalAmountInvested.toDouble)) }.toMap

val grp = data.groupBy(_.sym)

// % Return
// Map(MSFT -> -0.01641247313356875, HDP -> 0.09192200557103059, GE -> 0.10306700773655453)
val sym2Return = grp.mapValues { sym => 
    val sorted = sym.sortBy(_.Date.getTime)
    val value  =(sorted.last.Close / sorted.head.Close) -1 
    (math floor value * 100) / 100 * 100 // just formatting not formula
}

// Mean price
// Map(MSFT -> $51)
val sym2Mean = grp.mapValues { sym=>
    val closePrices = sym.map(_.Close)  
    val value = (closePrices.sum / closePrices.size)
    (math floor value * 100) / 100  // just formatting not formula
}

// Stnd Dev
// Map(MSFT -> 1.8314651772160067, HDP -> 1.1727299056426477, GE -> 1.1549489144687681)
val sym2StndDev = grp.mapValues { sym =>
    val closePrices = sym.map(_.Close)  
    val mean = (closePrices.sum / closePrices.size)
    var tmp = 0.0
    for (c <- closePrices) {
        tmp += Math.pow((c-mean),2)
    }
    var variance = tmp / closePrices.size
    val value = Math.sqrt(variance)
    (math floor value * 100) / 100  // just formatting not formula
}

// weighted portfolio, balance it, -3.12*(1/4) - 2.81*(1/4) - 1.83*(1/4) - 6.06*(1/4)

case class SymbolStats (sym:String, meanPrice: Double, avgReturn: Double, stndDev:Double, ratio: Double, initialAmt:Double)

val symbolStatsList = grp.map {
    case (symbol,v)=> 
    val meanPrice = sym2Mean.getOrElse(symbol,0.0)
    val stndDev = sym2StndDev.getOrElse(symbol,0.0)
    val avgReturn = sym2Return.getOrElse(symbol,0.0)
    val ratio = sym2PortfolioRatio.getOrElse(symbol,0.0)
    val initialAmt = investedAmountPerSymbol.getOrElse(symbol, 0.0)
    SymbolStats(symbol, meanPrice, avgReturn, stndDev, ratio, initialAmt)
}

sc.parallelize(symbolStatsList.toList).toDF.registerTempTable("stats")
sc.parallelize(sym2PortfolioRatio.toList).toDF.registerTempTable("sym2PortfolioRatio")

// -1.0*(1/4) + 6*(1/4) + 4*(1/4) =
// -.25 + 1.5 + .25 = 1.5
val portfolioPnl = symbolStatsList.foldLeft(0.0)((acc, elem) => acc + (elem.avgReturn * elem.ratio) )

val sym2AvgDlyReturn = grp.mapValues { 
    sym =>
    val sorted = sym.sortBy(_.Date.getTime).toArray

    var prev = sorted.head
    var perChangeInPriceList = List[Double]()
    var variance = 0.0
    for (curr <- sorted.tail) {
       perChangeInPriceList :+= (curr.Close / prev.Close) -1
       prev = curr
    }
    val mean = perChangeInPriceList.sum / perChangeInPriceList.length

    for (dlyChange <- perChangeInPriceList) {   
        variance += Math.pow(( dlyChange - mean),2) 
    }
    val stndDev = Math.sqrt ((variance / sorted.length))
   (mean, stndDev)
}

case class Simulation(symbol: String, interval: Int, perChange: Double, ratio: Double)
import org.apache.commons.math3.distribution._

var simsList = List[List[Simulation]]()

List.range(0, sims).map { s =>

  val projections = sym2AvgDlyReturn.mapValues {
    case (mean, stndDev ) =>
    val nd = new NormalDistribution(mean, stndDev) 
    for (day <- 0 until futureDays)  yield  (day, nd.sample)
  }

  val simulations = projections.map {  
    case (symbol, vec) =>
    val ratio =  sym2PortfolioRatio.getOrElse(symbol, 1.0)
    vec.map {
        case (day, perChange) => 
        Simulation(symbol, day, perChange, ratio) 
    }  
  }.flatten.toList

simsList :+= simulations
}

val simDF = sc.parallelize(simsList.head).toDF
simDF.registerTempTable("one_simulation")

// group by interval and calculate net daily change for percentiles
val portfolio = simDF.groupBy("interval").agg($"interval", sum($"perChange" * $"ratio").alias("totalChange")).orderBy("interval")
portfolio.registerTempTable("portfolio")

val percRows = sqlContext.sql("select explode (change) as perc  from (select percentile_approx(totalChange, array(0.05,0.50,0.95)) as change from portfolio ) t1").collectAsList

val worst   = percRows.get(0)(0).asInstanceOf[Double] * 100
val typical = percRows.get(1)(0).asInstanceOf[Double] * 100
val best    = percRows.get(2)(0).asInstanceOf[Double] * 100

var finalPortfolioValue = List[Double]()

simsList.map { s => s
    val simDF = sc.parallelize(s).toDF
    simDF.registerTempTable("simulation")

    // group by interval and calculate net daily change for percentiles
    val portfolio = simDF.groupBy("interval").agg($"interval", sum($"perChange" * $"ratio").alias("totalChange")).orderBy("interval")
    var dollarValue = totalinvestedAmt

    for (day <- portfolio.collect) {
        var netDailyChange = day(2).asInstanceOf[Double]
        dollarValue += ( dollarValue.toDouble * netDailyChange )
    }
    finalPortfolioValue :+= dollarValue
}
